    image: "https://images.unsplash.com/photo-1664286074176-5206ee5dc878?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxsZWF0aGVyJTIwYmVsdHxlbnwxfHx8fDE3NTkyNDU4NzZ8MA&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
    category: "Accessories",
    description: "Premium leather belt with classic buckle."
  },
  {
    id: "20",
    name: "Tote Bag",
    price: 59.99,
    originalPrice: 79.99,
    image: "https://images.unsplash.com/photo-1590084955567-ba857f41d437?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHx0b3RlJTIwYmFnJTIwY2FudmFzfGVufDF8fHx8MTc1OTE4MTc5MHww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
    category: "Accessories",
    isOnSale: true,
    description: "Spacious canvas tote bag perfect for daily use."
  },
  {
    id: "21",
    name: "Premium Cotton T-Shirt",
    price: 34.99,
    originalPrice: 44.99,
    image: "https://images.unsplash.com/photo-1503256575996-7cbe509190b7?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxibHVlJTIwdHNoaXJ0JTIwbWVufGVufDF8fHx8MTc1OTM1MzcxN3ww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
    images: [
      "https://images.unsplash.com/photo-1503256575996-7cbe509190b7?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxibHVlJTIwdHNoaXJ0JTIwbWVufGVufDF8fHx8MTc1OTM1MzcxN3ww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
      "https://images.unsplash.com/photo-1650314094468-b8f9594c5a46?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxibHVlJTIwdHNoaXJ0JTIwYmFja3xlbnwxfHx8fDE3NTkzNTM3MjN8MA&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral"
    ],
    category: "Men's Clothing",
    isOnSale: true,
    isNew: true,
    description: "Premium quality cotton t-shirt available in multiple vibrant colors. Each color variant comes with its own detailed product photos."
  },
  {
    id: "22",
    name: "Urban Hoodie",
    price: 79.99,
    image: "https://images.unsplash.com/photo-1688111421205-a0a85415b224?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxob29kaWUlMjBtZW4lMjBmYXNoaW9ufGVufDF8fHx8MTc1OTQxNDk0N3ww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
    category: "Men's Clothing",
    isNew: true,
    description: "Comfortable urban hoodie with modern streetwear style and premium fabric."
  },
  {
    id: "23",
    name: "Leather Crossbody Bag",
    price: 69.99,
    originalPrice: 99.99,
    image: "https://images.unsplash.com/photo-1709899629440-64da054379d4?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&ixid=M3w3Nzg4Nzd8MHwxfHNlYXJjaHwxfHxjcm9zc2JvZHklMjBiYWclMjBsZWF0aGVyfGVufDF8fHx8MTc1OTQxNDk0OHww&ixlib=rb-4.1.0&q=80&w=1080&utm_source=figma&utm_medium=referral",
    category: "Accessories",
    isOnSale: true,
    description: "Stylish leather crossbody bag perfect for daily essentials."
  }
];

function AppContent() {
  const { user } = useUser();
  const { isSellerMode, setIsSellerMode, products: sellerProducts, recordSale } = useSeller();
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [wishlistItems, setWishlistItems] = useState<string[]>([]);

  useEffect(() => {
    (async () => {
      if (user?.id) {
        try {
          const data = await loadUserData(user.id);
          setWishlistItems(data.wishlist || []);
          setCartItems(data.cart || []);
        } catch {}
      } else {
        setWishlistItems([]);
        setCartItems([]);
      }
    })();
  }, [user?.id]);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isAuthDialogOpen, setIsAuthDialogOpen] = useState(false);
  const [isOrderHistoryOpen, setIsOrderHistoryOpen] = useState(false);
  const [isWishlistOpen, setIsWishlistOpen] = useState(false);
  const [isCheckoutOpen, setIsCheckoutOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [isProductDetailOpen, setIsProductDetailOpen] = useState(false);
  const [currentView, setCurrentView] = useState<'main' | 'search' | 'category'>('main');
  const [currentCategory, setCurrentCategory] = useState<string | null>(null);
  const [productReviews, setProductReviews] = useState<{ [productId: string]: Review[] }>(sampleReviews);
  const [bottomNavActive, setBottomNavActive] = useState<'home' | 'wishlist' | 'cart' | 'seller'>('home');
  
  // Quick links state
  const [isAboutOpen, setIsAboutOpen] = useState(false);
  const [isContactOpen, setIsContactOpen] = useState(false);
  const [isSizeGuideOpen, setIsSizeGuideOpen] = useState(false);
  const [isShippingInfoOpen, setIsShippingInfoOpen] = useState(false);
  const [isReturnsOpen, setIsReturnsOpen] = useState(false);
  const [isPrivacyPolicyOpen, setIsPrivacyPolicyOpen] = useState(false);
  const [isTermsOpen, setIsTermsOpen] = useState(false);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);
  const [isFeedbackOpen, setIsFeedbackOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  const [filters, setFilters] = useState<FilterOptions>({
    searchQuery: '',
    categories: [],
    colors: [],
    priceRange: [0, 200],
    onSale: false,
    newArrivals: false,
    minRating: 0,
    sortBy: 'name'
  });

  const cartItemCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  // Use seller products (which sync with buyer view)
  const displayProducts = useMemo(() => {
    return sellerProducts.map(product => ({
      id: product.id,
      name: product.name,
      price: product.price,
      originalPrice: product.originalPrice,
      image: product.image,
      images: product.images,
      category: product.category,
      description: product.description,
      isOnSale: product.isOnSale,
      isNew: product.isNew,
      reviews: productReviews[product.id] || [],
      averageRating: productReviews[product.id] 
        ? productReviews[product.id].reduce((sum, review) => sum + review.rating, 0) / productReviews[product.id].length
        : 0
    } as Product));
  }, [sellerProducts, productReviews]);

  // Enhanced products with reviews
  const productsWithReviews = displayProducts;

  // Filter and sort products
  const filteredProducts = useMemo(() => {
    let filtered = productsWithReviews;

    // Category view filter (from navigation)
    if (currentCategory && currentCategory !== 'sale' && currentCategory !== 'new') {
      filtered = filtered.filter(product => product.category === currentCategory);
    } else if (currentCategory === 'sale') {
      filtered = filtered.filter(product => product.isOnSale);
    } else if (currentCategory === 'new') {
      filtered = filtered.filter(product => product.isNew);
    }

    // Search filter
    if (filters.searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(filters.searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(filters.searchQuery.toLowerCase()) ||
        product.description?.toLowerCase().includes(filters.searchQuery.toLowerCase())
      );
    }

    // Category filter (from filters panel)
    if (filters.categories.length > 0) {
      filtered = filtered.filter(product => filters.categories.includes(product.category));
    }

    // Color filter
    if (filters.colors.length > 0) {
      filtered = filtered.filter(product => {
        const productColors = sellerProducts.find(p => p.id === product.id)?.availableColors || [];
        return filters.colors.some(color => productColors.includes(color));
      });
    }

    // Price filter
    filtered = filtered.filter(product => 
      product.price >= filters.priceRange[0] && product.price <= filters.priceRange[1]
    );

    // Special filters
    if (filters.onSale) {
      filtered = filtered.filter(product => product.isOnSale);
    }

    if (filters.newArrivals) {
      filtered = filtered.filter(product => product.isNew);
    }

    // Rating filter
    if (filters.minRating > 0) {
      filtered = filtered.filter(product => (product.averageRating || 0) >= filters.minRating);
    }

    // Sort
    switch (filters.sortBy) {
      case 'price-asc':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-desc':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        filtered.sort((a, b) => (b.averageRating || 0) - (a.averageRating || 0));
        break;
      case 'newest':
        filtered.sort((a, b) => (b.isNew ? 1 : 0) - (a.isNew ? 1 : 0));
        break;
      default:
        filtered.sort((a, b) => a.name.localeCompare(b.name));
    }

    return filtered;
  }, [productsWithReviews, filters, currentCategory]);

  const categories = Array.from(new Set(sampleProducts.map(p => p.category)));
  const availableColors = Array.from(new Set(displayProducts.flatMap(p => 
    sellerProducts.find(sp => sp.id === p.id)?.availableColors || []
  )));

  const handleAddToCart = (product: Product, color?: string, size?: string) => {
    setCartItems(prev => {
      const existingItem = prev.find(item => item.id === product.id && item.selectedColor === color);
      const updated = existingItem
        ? prev.map(item =>
            item.id === product.id && item.selectedColor === color
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        : [...prev, { ...product, quantity: 1, selectedColor: color, selectedSize: size }];
      if (user?.id) { try { saveCart(user.id, updated); } catch {} }
      return updated;
    });
    const colorText = color ? ` (${color})` : '';
    toast.success(`${product.name}${colorText} added to cart`);
  };

  const handleUpdateQuantity = (productId: string, quantity: number) => {
    if (quantity <= 0) {
      handleRemoveItem(productId);
      return;
    }
    
    setCartItems(prev => {
      const updated = prev.map(item =>
        item.id === productId
          ? { ...item, quantity }
          : item
      );
      if (user?.id) { try { saveCart(user.id, updated); } catch {} }
      return updated;
    });
  };

  const handleRemoveItem = (productId: string) => {
    const item = cartItems.find(item => item.id === productId);
    setCartItems(prev => {
      const updated = prev.filter(item => item.id !== productId);
      if (user?.id) { try { saveCart(user.id, updated); } catch {} }
      return updated;
    });
    if (item) {
      toast.success(`${item.name} removed from cart`);
    }
  };

  const handleToggleWishlist = (productId: string) => {
    const product = sampleProducts.find(p => p.id === productId);
    setWishlistItems(prev => {
      const next = prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId];
      if (prev.includes(productId)) {
        toast.success(`${product?.name} removed from wishlist`);
      } else {
        toast.success(`${product?.name} added to wishlist`);
      }
      if (user?.id) { try { saveWishlist(user.id, next); } catch {} }
      return next;
    });
  };

  const handleAddReview = (productId: string, review: Omit<Review, 'id' | 'date' | 'helpful'>) => {
    const newReview: Review = {
      ...review,
      id: `r${Date.now()}`,
      date: new Date().toISOString().split('T')[0],
      helpful: 0
    };

    setProductReviews(prev => ({
      ...prev,
      [productId]: [...(prev[productId] || []), newReview]
    }));

    import("./services/reviews").then(({ addProductReview }) => {
      try { addProductReview(productId, newReview); } catch {}
    });
  };

  const handleCategoryChange = (category: string | null) => {
    setCurrentCategory(category);
    if (category) {
      setCurrentView('category');
    } else {
      setCurrentView('main');
    }
    // Clear search when changing category
    setFilters(prev => ({ ...prev, searchQuery: '' }));
  };

  const handleQuickLinkClick = (link: string) => {
    switch (link) {
      case 'about':
        setIsAboutOpen(true);
        break;
      case 'contact':
        setIsContactOpen(true);
        break;
      case 'size-guide':
        setIsSizeGuideOpen(true);
        break;
      case 'shipping':
        setIsShippingInfoOpen(true);
        break;
      case 'returns':
        setIsReturnsOpen(true);
        break;
      case 'privacy':
        setIsPrivacyPolicyOpen(true);
        break;
      case 'terms':
        setIsTermsOpen(true);
        break;
    }
  };

  const handleSearchChange = (query: string) => {
    setFilters(prev => ({ ...prev, searchQuery: query }));
    if (query.trim()) {
      setCurrentView('search');
      setCurrentCategory(null);
    } else if (filters.categories.length === 0 && filters.colors.length === 0 && !filters.onSale && !filters.newArrivals && filters.minRating === 0) {
      if (currentCategory) {
        setCurrentView('category');
      } else {
        setCurrentView('main');
        setBottomNavActive('home');
      }
    }
  };

  const handleClearFilters = () => {
    setFilters({
      searchQuery: '',
      categories: [],
      colors: [],
      priceRange: [0, 200],
      onSale: false,
      newArrivals: false,
      minRating: 0,
      sortBy: 'name'
    });
    if (currentCategory) {
      setCurrentView('category');
    } else {
      setCurrentView('main');
    }
  };

  const handleCheckout = () => {
    setIsCartOpen(false);
    setIsCheckoutOpen(true);
  };

  const handleOrderComplete = () => {
    // Record the sale
    const orderId = `ORDER-${Date.now()}`;
    const saleItems = cartItems.map(item => {
      const sellerProduct = sellerProducts.find(p => p.id === item.id);
      const variant = sellerProduct?.variants.find(v => 
        v.color === item.selectedColor && v.size === item.selectedSize
      );
      
      return {
        productId: item.id,
        productName: item.name,
        variantId: variant?.id,
        color: item.selectedColor,
        size: item.selectedSize,
        quantity: item.quantity,
        price: item.price,
        category: item.category
      };
    });

    recordSale(saleItems, orderId);

    setIsCheckoutOpen(false);
    setCartItems([]);
    toast.success('Order placed successfully!');
  };

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
    setIsProductDetailOpen(true);
  };

  const showSearchResults = currentView === 'search' || filters.searchQuery.trim() !== '';
  const showCategoryView = currentView === 'category' && currentCategory !== null;

  // Get category title
  const getCategoryTitle = () => {
    if (currentCategory === 'sale') return 'Sale';
    if (currentCategory === 'new') return 'New Arrivals';
    return currentCategory || 'All Products';
  };

  const handleSellerModeToggle = () => {
    // Only allow seller mode if user is a seller
    if (!user?.isSeller) {
      toast.error('You need a seller account to access the Seller Dashboard');
      return;
    }
    
    setIsSellerMode(!isSellerMode);
    // Reset view when switching modes
    if (!isSellerMode) {
      setCurrentView('main');
      setCurrentCategory(null);
    }
  };

  // If in seller mode, show seller dashboard (only if user is a seller)
  if (isSellerMode && user?.isSeller) {
    return (
      <div className="min-h-screen bg-background">
        <SellerDashboard onBackToBuyer={() => setIsSellerMode(false)} />
        <Toaster />
      </div>
    );
  }
  
  // If seller mode is active but user is not a seller, reset to buyer mode
  if (isSellerMode && !user?.isSeller) {
    setIsSellerMode(false);
  }

  return (
    <div className="min-h-screen bg-background">
      {isCheckoutOpen ? (
        <Checkout
          cartItems={cartItems}
          onBack={() => setIsCheckoutOpen(false)}
          onOrderComplete={handleOrderComplete}
        />
      ) : (
        <>
          <div className={`sidebar-push-content ${isCartOpen || isWishlistOpen ? 'pushed' : ''}`}>
          <Header 
            cartItemCount={cartItemCount}
            wishlistItemCount={wishlistItems.length}
            onCartClick={() => setIsCartOpen(true)}
            onWishlistClick={() => setIsWishlistOpen(true)}
            onLoginClick={() => setIsAuthDialogOpen(true)}
            onOrdersClick={() => setIsOrderHistoryOpen(true)}
            onSellerModeToggle={handleSellerModeToggle}
            onSearchChange={handleSearchChange}
            onCategoryChange={handleCategoryChange}
            currentCategory={currentCategory}
            onNotificationsClick={() => setIsNotificationsOpen(true)}
            onFeedbackClick={() => setIsFeedbackOpen(true)}
            onSettingsClick={() => setIsSettingsOpen(true)}
          />
          
          <main>
            {!showSearchResults && !showCategoryView ? (
              <>
                <HeroSection 
                  onShopNow={() => {
                    // Scrolling is handled in HeroSection component
                  }}
                  onViewCollection={() => {
                    // Scrolling is handled in HeroSection component
                  }}
                />
                <TopProducts
                  products={productsWithReviews}
                  onAddToCart={handleAddToCart}
                  onToggleWishlist={handleToggleWishlist}
                  wishlistItems={wishlistItems}
                  onProductClick={handleProductClick}
                  limit={6}
                />
                <FeaturedProducts
                  products={productsWithReviews}
                  onAddToCart={handleAddToCart}
                  onToggleWishlist={handleToggleWishlist}
                  wishlistItems={wishlistItems}
                  onProductClick={handleProductClick}
                />
                <CategoriesSection onCategoryChange={handleCategoryChange} />
              </>
            ) : showCategoryView ? (
              <>
                <CategoryHero category={currentCategory} productCount={filteredProducts.length} />
                <div className="container mx-auto px-3 md:px-4 py-6 md:py-8">
                  <div className="mb-6 md:mb-8">
                    <h2 className="text-xl md:text-2xl font-bold mb-4 text-indigo-700 dark:text-indigo-300">{getCategoryTitle()}</h2>
                    <SearchAndFilters
                      filters={filters}
                      onFiltersChange={setFilters}
                      onClearFilters={handleClearFilters}
                      categories={categories}
                      colors={availableColors}
                      hideCategories={true}
                    />
                  </div>

                  {filteredProducts.length > 0 ? (
                    <FeaturedProducts
                      products={filteredProducts}
                      onAddToCart={handleAddToCart}
                      onToggleWishlist={handleToggleWishlist}
                      wishlistItems={wishlistItems}
                      onProductClick={handleProductClick}
                    />
                  ) : (
                    <div className="text-center py-12">
                      <p className="text-muted-foreground text-lg">No products found</p>
                      <p className="text-sm text-muted-foreground mt-2">
                        Try adjusting your filters
                      </p>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="container mx-auto px-3 md:px-4 py-6 md:py-8">
                <div className="mb-6 md:mb-8">
                  <h2 className="text-xl md:text-2xl font-bold mb-4">
                    {filters.searchQuery ? `Search results for "${filters.searchQuery}"` : 'All Products'}
                  </h2>
                  <SearchAndFilters
                    filters={filters}
                    onFiltersChange={setFilters}
                    onClearFilters={handleClearFilters}
                    categories={categories}
                    colors={availableColors}
                  />
                </div>

                {filteredProducts.length > 0 ? (
                  <FeaturedProducts
                    products={filteredProducts}
                    onAddToCart={handleAddToCart}
                    onToggleWishlist={handleToggleWishlist}
                    wishlistItems={wishlistItems}
                    onProductClick={handleProductClick}
                  />
                ) : (
                  <div className="text-center py-12">
                    <p className="text-muted-foreground text-lg">No products found</p>
                    <p className="text-sm text-muted-foreground mt-2">
                      Try adjusting your filters or search terms
                    </p>
                  </div>
                )}
              </div>
            )}
          </main>
          
          <Footer 
            onCategoryChange={handleCategoryChange} 
            onQuickLinkClick={handleQuickLinkClick}
            onFeedbackClick={() => setIsFeedbackOpen(true)}
          />
          </div>

          {/* Mobile Bottom Navigation */}
          <BottomNav
            cartItemCount={cartItemCount}
            wishlistItemCount={wishlistItems.length}
            onHomeClick={() => {
              setCurrentView('main');
              setCurrentCategory(null);
              setBottomNavActive('home');
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
            onWishlistClick={() => {
              setIsWishlistOpen(true);
              setBottomNavActive('wishlist');
            }}
            onCartClick={() => {
              setIsCartOpen(true);
              setBottomNavActive('cart');
            }}
            onSellerDashboardClick={user?.isSeller ? () => {
              handleSellerModeToggle();
              setBottomNavActive('seller');
            } : undefined}
            showSellerDashboard={user?.isSeller}
            activeTab={bottomNavActive}
          />
        </>
      )}
      
      {/* Dialogs and Sheets */}
      <CartSheet
        isOpen={isCartOpen}
        onClose={() => setIsCartOpen(false)}
        cartItems={cartItems}
        onUpdateQuantity={handleUpdateQuantity}
        onRemoveItem={handleRemoveItem}
        onCheckout={handleCheckout}
      />

      <AuthDialog
        isOpen={isAuthDialogOpen}
        onClose={() => setIsAuthDialogOpen(false)}
      />

      <OrderHistory
        isOpen={isOrderHistoryOpen}
        onClose={() => setIsOrderHistoryOpen(false)}
      />

      <Wishlist
        isOpen={isWishlistOpen}
        onClose={() => setIsWishlistOpen(false)}
        wishlistItems={wishlistItems}
        products={productsWithReviews}
        onRemoveFromWishlist={(productId) => {
          setWishlistItems(prev => prev.filter(id => id !== productId));
          const product = productsWithReviews.find(p => p.id === productId);
          if (product) {
            toast.success(`${product.name} removed from wishlist`);
          }
        }}
        onAddToCart={handleAddToCart}
      />

      <ProductDetailModal
        product={selectedProduct}
        isOpen={isProductDetailOpen}
        onClose={() => {
          setIsProductDetailOpen(false);
          setSelectedProduct(null);
        }}
        onAddToCart={handleAddToCart}
        onToggleWishlist={handleToggleWishlist}
        isInWishlist={selectedProduct ? wishlistItems.includes(selectedProduct.id) : false}
        onAddReview={(review) => {
          if (selectedProduct) {
            handleAddReview(selectedProduct.id, review);
          }
        }}
      />

      {/* Quick Link Modals */}
      <AboutUs 
        isOpen={isAboutOpen} 
        onClose={() => setIsAboutOpen(false)} 
      />
      
      <Contact 
        isOpen={isContactOpen} 
        onClose={() => setIsContactOpen(false)} 
      />
      
      <SizeGuide 
        isOpen={isSizeGuideOpen} 
        onClose={() => setIsSizeGuideOpen(false)} 
      />
      
      <ShippingInfo 
        isOpen={isShippingInfoOpen} 
        onClose={() => setIsShippingInfoOpen(false)} 
      />
      
      <Returns 
        isOpen={isReturnsOpen} 
        onClose={() => setIsReturnsOpen(false)} 
      />

      <PrivacyPolicy
        isOpen={isPrivacyPolicyOpen}
        onClose={() => setIsPrivacyPolicyOpen(false)}
      />

      <TermsOfService
        isOpen={isTermsOpen}
        onClose={() => setIsTermsOpen(false)}
      />

      <NotificationSubscription
        isOpen={isNotificationsOpen}
        onClose={() => setIsNotificationsOpen(false)}
      />

      <CustomerFeedback
        isOpen={isFeedbackOpen}
        onClose={() => setIsFeedbackOpen(false)}
      />

      <Settings
        isOpen={isSettingsOpen}
        onClose={() => setIsSettingsOpen(false)}
        onFeedbackClick={() => setIsFeedbackOpen(true)}
      />

      <CookieConsent
        onAccept={() => {
          // Handle cookie acceptance - enable analytics, etc.
          console.log('Cookies accepted');
        }}
        onDecline={() => {
          // Handle cookie decline - disable optional cookies
          console.log('Optional cookies declined');
        }}
      />

      <Toaster />
    </div>
  );
}

export default function App() {
  return (
    <ThemeProvider>
      <UserProvider>
        <SellerProvider>
          <AppContent />
        </SellerProvider>
      </UserProvider>
    </ThemeProvider>
  );
}
