type Fix<T> = { [P in keyof T]: T[P] | null };

// It seems that the types for UrlWithStringQuery are not correct.
export type UrlLike = Fix<UrlWithStringQuery>;

export function emptyUrl(): UrlLike {
  return {
    query: null,
    port: null,
    auth: null,
    hash: null,
    host: null,
    hostname: null,
    href: null,
    path: null,
    pathname: null,
    protocol: null,
    search: null,
    slashes: null,
