export const SDK_VERSION = '8.2.8';
import { SDK_VERSION } from '../sdk-version';

if (typeof window !== 'undefined') {
  window.parent?.postMessage(
    {
      type: 'builder.isReactSdk',
      data: {
        value: true,
        supportsPatchUpdates: 'v4',
        supportsCustomBreakpoints: true,
        supportsXSmallBreakpoint: true,
        supportsGlobalSymbols: true,
        blockLevelPersonalization: true,
        version: SDK_VERSION,
      },
    },
    '*'
  );
}
// TODO: pull from builder internal utils
export const fastClone = <T extends object>(obj: T): T => JSON.parse(JSON.stringify(obj));

export const omit = <T extends object>(obj: T, ...values: (keyof T)[]): Partial<T> => {
  const newObject = Object.assign({}, obj);
  for (const key of values) {
    delete (newObject as any)[key];
  }
  return newObject;
};
import { fastClone } from '../functions/utils';

export type Size = 'large' | 'medium' | 'small' | 'xsmall';
export const sizeNames: Size[] = ['xsmall', 'small', 'medium', 'large'];

// TODO: put in @builder.io/core
const sizes = {
  xsmall: {
    min: 0,
    default: 160,
    max: 320,
  },
  small: {
    min: 321,
    default: 321,
    max: 640,
  },
  medium: {
    min: 641,
    default: 642,
    max: 991,
  },
  large: {
    min: 990,
    default: 991,
    max: 1200,
  },
  getWidthForSize(size: Size) {
    return this[size].default;
  },
  getSizeForWidth(width: number) {
    for (const size of sizeNames) {
      const value = this[size];
      if (width <= value.max) {
        return size;
      }
    }
    return 'large';
  },
};
export type Sizes = typeof sizes;

export interface Breakpoints {
  xsmall?: number;
  small?: number;
  medium?: number;
}

export const getSizesForBreakpoints = (breakpoints: Breakpoints) => {
  const newSizes = {
    ...sizes, // Note: this helps get the function from sizes
    ...fastClone(sizes), // Note: this helps to get a deep clone of fields like small, medium etc
  };

  if (!breakpoints) {
    return newSizes;
  }

  const { xsmall, small, medium } = breakpoints;

  if (xsmall) {
    const xsmallMin = Math.floor(xsmall / 2);
    newSizes.xsmall = {
      max: xsmall,
      min: xsmallMin,
      default: xsmallMin + 1,
    };
  }

  if (!small || !medium) {
    return newSizes;
  }

  const smallMin = xsmall ? newSizes.xsmall.max + 1 : Math.floor(small / 2);
  newSizes.small = {
    max: small,
    min: smallMin,
    default: smallMin + 1,
  };

  const mediumMin = newSizes.small.max + 1;
  newSizes.medium = {
    max: medium,
    min: mediumMin,
    default: mediumMin + 1,
  };

  const largeMin = newSizes.medium.max + 1;
  newSizes.large = {
    max: 2000, // TODO: decide upper limit
    min: largeMin,
    default: largeMin + 1,
  };

  return newSizes;
};
export const set = (obj: any, _path: string | string[], value: any) => {
  if (Object(obj) !== obj) {
    return obj;
  }
  const path: string[] = Array.isArray(_path)
    ? _path
    : (_path.toString().match(/[^.[\]]+/g) as string[]);

  path
    .slice(0, -1)
    .reduce(
      (a, c, i) =>
        Object(a[c]) === a[c]
          ? a[c]
          : (a[c] = Math.abs(Number(path[i + 1])) >> 0 === +path[i + 1] ? [] : {}),
      obj
    )[path[path.length - 1]] = value;
  return obj;
};
'use strict';

import { Builder } from '@builder.io/sdk';

const noop = () => null;
// Allow us to require things dynamically safe from webpack bundling

export let safeDynamicRequire: typeof require;

/*
 * The if condition below used to be
 *
 *     if (typeof globalThis.require === "function")
 *
 * That broke in case Builder was running on the server (Next, SSR use-cases) where
 * globalThis.require was undefined. Avoiding use of Builder.isServer for Cloudflare worker cases
 * That said, if change it to
 *
 * if (typeof require === 'function') {
 *   localSafeDynamicRequire = eval('require');
 * }
 *
 * Then the TSC / rollup compiler over-optimizes and replaces the if condition with true always
 * causing it to blow up on the client side. Hence this convoluted way of doing it.
 *
 * In Summary:
 *
 * 1. Node -> globalThis.require does not work
 * 2. Cloudflare edge -> only globalThis.require works
 */
if (
  typeof globalThis?.require === 'function' ||
  (Builder.isServer && typeof require === 'function')
) {
  try {
    /*
      This is a hack to get around webpack bundling the require function, which is not available in the browser
      Needs to be eval'd to avoid webpack bundling it
    */
    safeDynamicRequire = eval('require');
  } catch (e) {
    /* 
      This is a patch for enviornments where eval is not allowed, like Shopify-hydrogen storefront
      Relevant issue : https://github.com/BuilderIO/builder-shopify-hydrogen/issues/12
    */
    if (globalThis?.require) {
      safeDynamicRequire = globalThis.require;
    } else {
      // @ts-ignore
      safeDynamicRequire = noop;
    }
  }
}

safeDynamicRequire ??= noop as any;
export function isDebug(): boolean {
  return Boolean(
    typeof process !== 'undefined' && typeof process.env !== 'undefined' && process.env.DEBUG
  );
}
function isNodeRuntime(): boolean {
  return typeof process !== 'undefined' && Boolean(process?.versions?.node);
}

export const shouldForceBrowserRuntimeInNode = () => {
  if (!isNodeRuntime()) return false;

  const isArm64 = process.arch === 'arm64';
  const isNode20 = process.version.startsWith('v20');

  const hasNoNodeSnapshotNodeOption = process.env.NODE_OPTIONS?.includes('--no-node-snapshot');

  if (isArm64 && isNode20 && !hasNoNodeSnapshotNodeOption) {
    console.log(`Skipping usage of \`isolated-vm\` to avoid crashes in Node v20 on an arm64 machine.
    If you would like to use the \`isolated-vm\` package on this machine, please provide the \`NODE_OPTIONS=--no-node-snapshot\` config to your Node process.
    See https://github.com/BuilderIO/builder/blob/main/packages/react/README.md#node-v20--m1-macs-apple-silicon-support for more information.
    `);

    return true;
  }

  return false;
};
import { Builder, builder } from '@builder.io/sdk';
import { safeDynamicRequire } from './safe-dynamic-require';
import { isDebug } from './is-debug';
import { shouldForceBrowserRuntimeInNode } from './should-force-browser-runtime-in-node';

const fnCache: { [key: string]: BuilderEvanFunction | undefined } = {};

type BuilderEvanFunction = (
  state: object,
  event?: Event | undefined | null,
  block?: any,
  builder?: Builder,
  Device?: any,
  update?: Function | null,
  _Builder?: typeof Builder,
  context?: object
