  const clonedObj: any = {};

  for (const key in obj) {
    if (Object.prototype.hasOwnProperty.call(obj, key)) {
      if (key === 'meta') {
        clonedObj.meta = obj['meta'];
      } else {
        clonedObj[key] = deepCloneWithConditions(obj[key]);
      }
    }
  }

  return clonedObj;
